def elastic_search(category=None):
    client = Elasticsearch(host="localhost", port=9200)

    query_all = {
        'size': 10000,
        'query': {}
    }

    if category is None:
        query_all['query']['match_all'] = {}
    else:
        query_all['query']['bool'] = {
             "filter": [
               {
                 "match": {
                   "category": category
                 }
               }
             ]
        }

    resp = client.search(
        index="my-index",
        body=query_all
        )
